const coin = $input.first().json.chatInput.toUpperCase();
const symbol = coin + 'USDT'; 
const limit = 500;
const intervals = [
  '15m', // for 15 minutes timeframe
  '1h', // for 1 hour timeframe
  '4h', // for 4 hours timeframe
];

const currentCandles = {
  interval: '1m',
  limit: 100
}

return [{
    coin,
    symbol,
    limit,
    intervals,
    currentCandles
  }];
