parameters.jsCode:
const raw = ($input.first().json.chatInput || $input.first().json.message?.text || '').trim();
if (!raw) throw new Error('No coin symbol provided. Send a symbol like BTC, ETH, or SOL.');

// Strip common suffixes if user already typed full pair
const coin = raw.toUpperCase().replace(/USDT$/, '').replace(/[^A-Z0-9]/g, '');
if (!coin) throw new Error('Invalid symbol. Send a coin like BTC, ETH, or SOL.');

const symbol = coin + 'USDT';
const chatId = $input.first().json.message?.chat?.id || '5572228344';
const limit = 500;

const intervals = [
  '15m', // for 15 minutes timeframe
  '1h', // for 1 hour timeframe
  '4h', // for 4 hours timeframe
  '1m', // for 1 minute timeframe
];

return [{
    chatId,
    coin,
    symbol,
    limit,
    intervals,
  }];

